'use strict';

var zscale = require('./../');

var test = function test(title, d, input) {
	var json = JSON.stringify(d.value);
	var func = zscale.generate(d);
	console.log('\n\n\t ' + title + '\u5173\u7CFB\n\t \u914D\u7F6E: ' + json + '\n\t \u8F93\u5165: ' + input + '\n\t \u8F93\u51FA: ' + func(input) + '\n\n\t');
};

var getSchema = function getSchema(type) {
	return {
		type: 'gradient',
		value: {
			domain: {
				min: 0.001,
				max: 2
			},
			range: {
				min: 0.001,
				max: 3
			},
			easing: type
		}
	};
};

var input = 1.1;

[{
	name: '线性',
	type: 'linear'
}, {
	name: '幂次',
	type: 'pow'
}, {
	name: '对数',
	type: 'log'
}].forEach(function (d) {
	return test(d.name, getSchema(d.type), input);
});